package ch05_3_for_while_and_do_while;

/**
 * @author kimhajin
 *
 */
public class P05_5_01 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int mul = 1;
		
		for (int i = 1; i <= 10; ++i) {
			mul *= i;
		}
		
		System.out.println("Product: " + mul);
		
	}

}
